import React, { useState } from 'react';
import './Post.css'

 

export default function Post(){

const [displayPost, setDisplayPost]= useState([

    {
        id:1,
        title:'Post one',
        img:'https://cdn.blossominggifts.com/media/catalog/product/cache/7/small_image/400x400/9df78eab33525d08d6e5fb8d27136e95/l/e/letter_box_candy_roses.jpg',
        com:'Nice'
        
        //   comm:[
        //       {com:'nice'},
        //       {com:'good'}
        //   ]
    },
    {
        id:2,
        title:'post two',
        img:'https://images.squarespace-cdn.com/content/v1/59fe3088ccc5c51065e606ab/1603327690675-ZYZIK7P3WVMBVRUT5OSO/theposypostsep20205727.jpg?format=1000w',
        com:'Good'
        
    // comm :[

    //         {com:'i love it'},
    //         {com:'hello'}
             
    //     ]
    }
])

// const [comm,setComm]= useState()
  

return(
  
   <>  
  
<AddPostForm setDisplayPost={setDisplayPost}/>

<div class='po'> 
        
 {displayPost.map((post)=>(

 <div class='post'>
     id={}
    <h4>  {post.title}</h4>
    <img src= {post.img} alt ='' width={150} height={150}/> 
     <p>{post.com}</p>
  

     {/* {post.comm.map((child)=>(
     
     <p> {child.com}</p>

     ))} */}
     

      

     <br/>


<AddCommint setDisplayPost={setDisplayPost}/>

   </div> 
 
 ))}
     
  
</div>

</>
);
 
 
}

function AddCommint(pro){

     
     const [co,setCo]=useState('')

    function handleCommint(e){
            e.preventDefault()

 pro.setDisplayPost(prv => prv.concat({id:Date.now(), com:co}))
 
 setCo('')
            alert('you add new commint.')
    }

    return(
<>

<form onSubmit={handleCommint}> 
<field>
<div class='commit'>
 
     <input value={co} onChange={e => setCo(e.target.value)} placeholder='add commint ..'/>
     <button onClick={handleDelete} class='Rbtn'>Remove Commint</button>
     {/* <button class='Abtn'>Add Comint</button> */}
      
     </div>
     </field>
     </form>
     </>
     )
 function handleDelete(){
     return(
         <>
         </>
     )
 }
}

 

function AddPostForm(props){

    const [title,setTitle]=useState()
    const [image,setImage]=useState()
 
function handleSubmit(e){
e.preventDefault()

props.setDisplayPost(prev => prev.concat({id:Date.now() ,title:title, img:image}))
setTitle('')
setImage('')
 
alert('you add new post form')
}

    return(
        <>

<form onSubmit={handleSubmit}>
<field>
<newPost>Add New Post .</newPost>

<input value={title} onChange={e => setTitle(e.target.value)} placeholder="The Title of Post."/>
<input value={image} onChange={e => setImage(e.target.value)} placeholder="The Link of Post."/>
<button>Add Post</button>

</field>
</form>
        </>
    )
}
