import './App.css';
import axios from 'axios'
//import ReactDOM from 'react-dom';
import Post from './component/Post';
import About from './component/About';
//import Button from 'react-bootstrap/Button'


import {Route ,BrowserRouter as Router ,Switch ,Link} from 'react-router-dom';

import React,{useEffect, useState} from 'react';


export default function App() {
  return (
      <> 
      <Router>

      <nav>
         <ul>
          <li> <Link to='/' exact> Post </Link></li>
          <li> <Link to='/About'>About </Link></li>
        </ul>
     </nav>   

     <TimeArea />
     <GetAxios/>

<br/>
<Switch>
<Route path='/' exact component={Post}/>
<Route path='/About' component={About}/>
</Switch>

      </Router>

      </>

  );
}

 function GetAxios(){

const [word,setWord]=useState('')
const [data,setData]=useState([])


  useEffect(()=>{

    axios.get(`http://worldtimeapi.org/api/timezone/America/Argentina/${word}`)
    .then((res)=>{console.log(res.data.datetime)

setData(res.data.datetime)


    })
     .catch(error=>console.log(error));
  } ,[word])

  return(

    <>

    <div className='Btn'>
<button onClick={()=>setWord('Salta')}>Dubai</button>

