import './Post.css'

const displayPost=[
    {
        title:'Post one',
        img:'https://cdn.blossominggifts.com/media/catalog/product/cache/7/small_image/400x400/9df78eab33525d08d6e5fb8d27136e95/l/e/letter_box_candy_roses.jpg'
    },
    {
        title:'post two',
        img:'https://images.squarespace-cdn.com/content/v1/59fe3088ccc5c51065e606ab/1603327690675-ZYZIK7P3WVMBVRUT5OSO/theposypostsep20205727.jpg?format=1000w'
    }
]


export default function Post(){

const [comint ,setComint]=useState([{}])




return(
  
   <>  
  

 
<AddPostForm/>

<div class='po'> 
        
 {displayPost.map((post)=>(

 <div class='post'>

    <h4>{post.title}</h4>
     <img src= {post.img} alt ='' width={150} height={150}/>
     <br/>

     <div class='commit'>
     <input placeholder='commint'/>
     <button class='Rbtn' onClick={RemoveCommint}>Remove</button>
     <button class='Abtn' onClick={AddCommint}>Add Comint</button>
     
     </div>
      
   </div> 
 
 ))}
     
  
</div>
</>
);
}
 
function AddPostForm(){


function handleSubmit(e){
e.preventDefault()
alert('you add new post form')
}

    return(
        <>

<form onSubmit={handleSubmit}>
<field>
<newPost>Add New Post .</newPost>

<input placeholder="The Title of Post."/>
<input placeholder="The Link of Post."/>
<button>Add Post</button>

</field>
</form>
        </>
    )
}

 function RemoveCommint(){
     alert('Your commint is removed.. ')
 } 

 function AddCommint(){
    alert('you add a new commint.. ')
 }
