import React, { useState } from 'react';
import './Post.css'

 

export default function Post(){

const [displayPost, setDisplayPost]= useState([

    {
        id:1,
        title:'Post one',
        img:'https://cdn.blossominggifts.com/media/catalog/product/cache/7/small_image/400x400/9df78eab33525d08d6e5fb8d27136e95/l/e/letter_box_candy_roses.jpg',
        com:'Nice'
    },
    {
        id:2,
        title:'post two',
        img:'https://images.squarespace-cdn.com/content/v1/59fe3088ccc5c51065e606ab/1603327690675-ZYZIK7P3WVMBVRUT5OSO/theposypostsep20205727.jpg?format=1000w',
        com:'Good'
    }
])

 

return(
  
   <>  
  
<AddPostForm setDisplayPost={setDisplayPost}/>
<ol> 
 <Pos/>

</ol>
 

</>
);
 function Pos(){

    function handleDelete(){
 
    }
     return(
         <>
<div class='po'> 
        
         
        {displayPost.map((post)=>(
       
        <div class='post'>
              
           <h4>  {post.title}</h4>
           <img src= {post.img} alt ='' width={150} height={150}/>  
           <br/>
          <p>{post.com}</p>
            <button onClick={()=>handleDelete()} >Remove comment</button>
          </div> 
           
        ))}
        
       </div>

         </>
     )
 }
 


function AddPostForm(props){

    const [title,setTitle]=useState()
    const [image,setImage]=useState()
    const [comment ,setComment]=useState()
 
function handleSubmit(e){
e.preventDefault()

props.setDisplayPost(prev => prev.concat({id:Date.now() ,title:title, img:image,com:comment}))
setTitle('')
setImage('')
setComment('')
 
alert('you add new post form')
}

    return(
        <>

<form onSubmit={handleSubmit}>
<field>
<newPost>Add New Post .</newPost>

<input value={title} onChange={e => setTitle(e.target.value)} placeholder="The Title of Post."/>
<input value={image} onChange={e => setImage(e.target.value)} placeholder="The Link of Post."/>
<input value={comment} onChange={e => setComment(e.target.value)} placeholder="Comment for Post."/>

<button>Add Post</button>

</field>
</form>
        </>
    )
}

}
